<?php defined('SYSPATH') or die('No direct script access.');/** * @version SVN: $Id:$ */class Controller_Site_Page extends Controller_Site{    public function action_index()    {        $page_url = $this->param('url');		$page = ORM::factory('Page')->get_page_by_url($page_url);        $this->set_metatags_and_content($page_url, 'page');		$this->template->set_layout('layout/site/global');    }    public function action_contacts()    {        //die('y');        //$this->set_metatags_and_content('', 'page');        $page_url = 'contacts';        //die($page_url);		//$page = ORM::factory('Page')->get_page_by_url($page_url);        $this->set_metatags_and_content($page_url, 'page');		$this->template->set_layout('layout/site/global');    }    public function action_saveFeedback()    {        $name = $this->request->post('name');        $email = $this->request->post('email');        $phone = $this->request->post('phone');        $message = $this->request->post('message');        $PDO = ORM::factory('Orders')->PDO();        $stmt = $PDO -> prepare("INSERT INTO feedback(name, email, text, phone) VALUES(:name, :email, :text, :phone)");        $stmt->bindParam(':name', $name);        $stmt->bindParam(':email', $email);        $stmt->bindParam(':text', $phone);        $stmt->bindParam(':phone', $message);        $stmt->execute();        exit(json_encode(array()));    }}