<?php defined('SYSPATH') or die('No direct script access.');class Controller_Site_Reviews extends Controller_Site{    public function action_add()    {		$this->set_metatags_and_content('', 'page');		$this->template->set_layout('layout/site/global');		if ($this->request->is_ajax()) {			$prod_id = $this->request->post('prod_id');			$name = $this->request->post('name');						$rating = $this->request->post('rating');			$content = $this->request->post('content');						$review = ORM::factory('Reviews');			$review->prod_id = $prod_id;			$review->name = $name;						$review->rating = $rating;			$review->content = $content;			$review->save();						$admin_message = View::factory('site/review/message', array(				'prod' => ORM::factory('Product')->fetchProdById($prod_id),				'name' => $name,								'name' => $rating,				'content' => $content 			))->render();						//Email::send('artem.skorokhod@tut.by','info@trip-shop.by', 'Новый отзыв', $admin_message, true);									exit(json_encode(array()));		}		$this->forward_404();    }}