<?php defined('SYSPATH') or die('No direct script access.');/** * @version SVN: $Id:$ * @author o.zgolich */class Form_Admin_Reviews extends CM_Form_Abstract{    protected function init()    {        $this->add_plugin(new CM_Form_Plugin_ORM());		$this->set_field('name', new CM_Field_String(), 0);		$this->set_field('rating', new CM_Field_String(), 10);		$this->set_field('content', new CM_Field_Text(), 20);		$this->set_field('prod_id', new CM_Field_Select_ORM(ORM::factory('Product')), 30);		$this->set_field('active', new CM_Field_Boolean(), 40);		$this->get_field('active')->set_raw_value(true); 				$fieldgroups = array(			'Основные данные' => array('name', 'content', 'rating','prod_id', 'active'),		);		$this->add_plugin(new CM_Form_Plugin_Fieldgroups($fieldgroups));    }}