<?php defined('SYSPATH') or die('No direct script access.');/** * @version SVN: $Id:$ */class Model_Orders extends ORM{    protected $_table_name = 'orders';	public $statuses = array(		1 => 'Новый',		2 => 'Подтверждён',		3 => 'Отправлен',		4 => 'Доставлен',		5 => 'Нет на складе',		6 => 'Возврат'	);    protected $_grid_columns = array(        'id' => null,		'name' => null,		'phone' => null,		'created_at' => null,		'status_name' => null,        'city' => null,        'price' => array(            'width' => '100',           'type' => 'template',           'template' => '<span class="label label-primary">${resultPrice}</span>'          ),        'product_info' => array(            'width' => '40',            'type' => 'link',            'route_str' => 'admin-orders:view?id=${id}',            'title' => '<i class="fa fa-cogs"></i>',            'color' => 'blue',            'alternative' => 'Детали заказа'        ),		'edit' => array(			'width' => '40',			'type' => 'link',			'route_str' => 'admin-orders:edit?id=${id}',			'title' => '<i class="fa fa-edit"></i>',			'color' => 'green',			'alternative' => 'Редактировать'		),        'delete' => array(            'width' => '40',            'type' => 'link',            'route_str' => 'admin-orders:delete?id=${id}',            'title' => '<i class="fa fa-trash-o"></i>',            'alternative' => 'Удалить',			'color' => 'red',            'confirm' => 'Вы уверены?'        )    );    public function labels()    {        return array(            'id' => 'Номер заказа',			'name' => 'Имя',			'phone' => 'Телефон',			'created_at' => 'Дата поступления',			'status_name' => 'Статус',			'status' => 'Статус',            'city' => 'Город',            'price' => 'Цена'        );    }    public function form()    {        return new Form_Admin_Order($this);    }	public function get_product_info()	{		return HTML::anchor('/ariol-admin/order/view/' . $this->id, 'Детали заказа <span class="glyphicon glyphicon-link"></span>');	}	public function get_status_name()	{        $status_value = '';		switch($this->statuses[$this->status]) {            case 'Новый':                $status_value = '<span class="label label-info">Новый</span>';            break;            case 'Подтверждён':                $status_value = '<span class="label label-primary">Подтверждён</span>';            break;            case 'Отправлен':                $status_value = '<span class="label label-warning">Отправлен</span>';            break;            case 'Доставлен':                $status_value = '<span class="label label-success">Доставлен</span>';            break;            case 'Нет на складе':                $status_value = '<span class="label label-default">Нет на складе</span>';            break;            case 'Возврат':                $status_value = '<span class="label label-danger">Возврат</span>';            break;        }        return $status_value;	}    public function send_message()    {        $this->statuses == 1;    }    public function sortable_fields()    {        return array(            'created_at',        );    }    public function save(Validation $validation = NULL)    {        parent::save($validation);        if ($this->status == 4) {            $order = $this;            $PDO = ORM::factory('Orders')->PDO();            $orders_flag = $PDO->query("SELECT success_flag FROM orders WHERE id = '{$order->id}'")->fetchAll(PDO::FETCH_ASSOC);            if ($orders_flag[0]['success_flag'] != 1) {                $time = time();                $time_end = $time + (86400 * 180);                $time_end = date("Y-m-d", $time_end);                $time_end_coupon = $time + (86400 * 60);                $time_end_coupon = date("Y-m-d", $time_end_coupon);                $full_price = 0;                $success_flag_query = "UPDATE orders SET success_flag = 1 WHERE id = '{$order->id}'";                $PDO->exec($success_flag_query);                $PDO = ORM::factory('OrderProduct')->PDO();                $query = "SELECT order_product.quantity, order_product.price                          FROM order_product                          WHERE order_id = '{$order->id}'";                $order_data = $PDO->query($query)->fetchAll(PDO::FETCH_ASSOC);                $discount = $PDO->query("SELECT coupons.code, coupons.discount, coupons.time_end                                    FROM coupons                                    WHERE coupons.order_id = '{$order->id}'")->fetch();                if ($discount['discount']) {                    $PDO_coupon = ORM::factory('Coupons')->PDO();                    $PDO_coupon->query("UPDATE coupons SET time_end = '{$time_end_coupon}', active = 1  WHERE order_id = '{$order->id}'");                    if ($order->email != '') {                        $user_message = View::factory('admin/order/usermessage_delivery', array(                            'name' => $order->name,                            'email' => $order->email,                            'phone' =>$order->phone,                            'adress' => $order->adress,                            'city' => $order->city,                            'index' => $order->index,                            'time' => $time_end,                            'discount' => $discount['discount'],                            'code' => $discount['code']                        ))->render();                        Helpers_Email::send($order->email, 'Новый заказ '.$order->name.' '.$order->phone, $user_message, true);                    }                }                $PDO_order_certificate = ORM::factory('OrderCertificate')->PDO();                $query_validity_certificate = "SELECT id, time_end                                                FROM order_certificate                                                WHERE order_id = '{$order->id}'";                $validity_data = $PDO_order_certificate->query($query_validity_certificate)->fetchAll(PDO::FETCH_ASSOC);                foreach($validity_data as $v_data)                {                    $time = time();                    $time_end_certificate = $time + (86400 * $v_data['time_end']);                    $time_end_certificate = date("Y-m-d", $time_end_certificate);                    $update_validity_query = "UPDATE order_certificate SET time_end = '{$time_end_certificate}', active = 1 WHERE id = '{$v_data['id']}'";                    $PDO_order_certificate->query($update_validity_query);                }            }        }    }    public function get_resultPrice()    {        $PDO_product = ORM::factory('Orders')->PDO();        $query = "SELECT  orders.id,                        orders.name,                        orders.email,                        orders.phone,                        orders.status,                        orders.created_at,                        orderproduct.prod_price,                        ordercertificate.cert_price,                        code_certificate,                        coupons.discount,                        order_certificate.to_amount,                        delivery                        FROM orders                      LEFT JOIN (SELECT order_id, product_id, SUM(quantity * price) as prod_price                      FROM order_product                      GROUP BY order_id) as orderproduct ON orderproduct.order_id = orders.id                      LEFT JOIN                      (SELECT order_id, SUM(price) as cert_price                      FROM order_certificate                      GROUP BY order_id) as  ordercertificate                      ON orders.id = ordercertificate.order_id                      LEFT JOIN  coupons ON coupons.code = orders.code_coupon                      LEFT JOIN order_certificate ON order_certificate.code = orders.code_certificate WHERE orders.id = '{$this->id}'";        $result = $PDO_product->query($query)->fetchAll(PDO::FETCH_ASSOC);        $price_delivery = 0;        foreach ($result as $item) {            $priceNotDiscount = $item['prod_price'] + $item['cert_price'];            switch($item['delivery']) {                case '2':                    if($priceNotDiscount < 600000) { $price_delivery = 30000;  } else { $price_delivery = 0;}                    break;                case '3':                    if($priceNotDiscount < 1000000) { $price_delivery = 50000; }  else { $price_delivery = 0;}                    break;            }            $price = $item['prod_price'];            if ($item['discount'] and !$item['to_amount'])                $price = $item['prod_price'] - ($item['prod_price'] / 100 * $item['discount']);            if ($item['to_amount'] and !$item['discount'])                $price = $item['prod_price'] - $item['to_amount'];            if ($item['discount'] and $item['to_amount'])                $price = ($item['prod_price'] - ($item['prod_price'] / 100 * $item['discount'])) - $item['to_amount'];            $price = $price + $item['cert_price'] + $price_delivery;            }       $price =  number_format($price, 0, '', ' ').'руб.';        return $price;        }}