<?php defined('SYSPATH') or die('No direct script access.');/** * @version SVN: $Id:$ */class Model_Reviews extends ORM{    protected $_table_name = 'reviews';		protected $_grid_columns = array(        'name' => array(            'type' => 'template',            'template' => '${name_value}'        ),        'content' => 'empty',        'active' => 'bool',        'created_at' => NULL,        'edit' => array(			'width' => '50',            'type' => 'link',            'route_str' => 'admin-reviews:edit?id=${id}',            'title' => '<i class="fa fa-pencil"></i>',			'color' => 'green',            'alternative' => 'Редактировать'        ),        'delete' => array(            'width' => '50',            'type' => 'link',            'route_str' => 'admin-reviews:delete?id=${id}',            'title' => '<i class="fa fa-trash-o"></i>',			'color' => 'red',            'alternative' => 'Удалить',            'confirm' => 'Вы уверены?'        )    );        public function labels()    {        return array(            'name' => 'Имя',            'title' => 'Заголовок',            'content' => 'Текст отзыва',            'active' => 'Статус',						'rating' => 'Рейтинг',            'created_at' => 'Последнее обновление'        );    }	    public function form()    {        return new Form_Admin_Reviews($this);    }    protected $_grid_options = array(        'order_by' => 'created_at',        'order_direction' => 'DESC'    );		public function getReviewsByProdId($id)	{		return $this->where('prod_id', '=', $id)->where('active', '=', 1)->find_all();				}	public function countRatingByProdId($id)	{		$rewiews = ORM::factory('Reviews')->where('active', '=', 1)->where('prod_id', '=', $id)->find_all()->as_array();			$counter = count($rewiews);		if($rewiews){			$counter_stars = 0;			foreach($rewiews as $item){				$counter_stars = $counter_stars + $item->rating;			}			return $counter_stars/$counter;		}				return 0;			}		public function countReviewsByProdId($id)	{		return $this->where('prod_id', '=', $id)->where('active', '=', 1)->count_all();				}    public function sortable_fields()    {        return array(            'name'        );    }    public function get_name_value()    {        return $this->name;    }}